import Navigation from './Navigation';

const Layout = props => {
  return (
    <LayoutContainer>
      <Navigation />
      {props.children}
    </LayoutContainer>
  );
};

import styled from 'styled-components';

const LayoutContainer = styled.div`
  font-size: 50px;
  /* color: ${({ theme }) => theme.colors.primary}; */
`;

const layoutStyle = {
  margin: 20,
  padding: 20,
  border: '1px solid #DDD'
};

export default Layout;
