import { useRouter } from 'next/router';
import fetch from 'isomorphic-unfetch';
import Layout from '../../src/components/Layout';
import { removeSource, formatDateTime } from '../../src/components/Card.js';

const News = props => {
  const router = useRouter();
  const filterNews = props.data.articles
    .filter(ele => ele.title.toLowerCase() === router.query.id)
    .map(ele => ele);

  console.log(filterNews);

  // const news = {
  //   title: removeSource(filterNews.title),
  //   titleUrl: filterNews.title.toLowerCase(),
  //   source: filterNews.source.name,
  //   sourceUrl: filterNews.url,
  //   author: filterNews.author,
  //   date: formatDateTime(filterNews.publishedAt),
  //   description: filterNews.description,
  //   image: filterNews.urlToImage
  // };

  return (
    <Layout>
      <h1>{router.query.id}</h1>
      <p>This is the blog post content.</p>
    </Layout>
  );
};

News.getInitialProps = async context => {
  const { id } = context.query;
  const res = await fetch(
    'https://newsapi.org/v2/top-headlines?country=id&apiKey=fe10d3dbecfe418baeb787b670337a86'
  );
  const data = await res.json();

  return {
    data
  };
};

export default News;
