import fetch from 'isomorphic-unfetch';
import GlobalProvider from '../context/context';
import Layout from '../components/Layout';
import Card from '../components/Card';

const index = props => {
  console.log(props);

  return (
    <Layout>
      {/* {props.news.map((ele, index) => {
        return <Card key={index} {...ele} />;
      })} */}
    </Layout>
  );
};

index.getInitialProps = async () => {
  const res = await fetch(
    'https://newsapi.org/v2/top-headlines?country=id&category=sports&apiKey=fe10d3dbecfe418baeb787b670337a86'
  );
  const data = await res.json();

  return {
    news: data.articles.map(item => item)
  };
};

export default GlobalProvider(index);
