import styled from 'styled-components';
import Link from 'next/link';

const formatDateTime = dateTime => {
  const date = dateTime.split('T')[0];
  const splitDate = date.split('-');
  const dateIdn = `${splitDate[2]}-${splitDate[1]}-${splitDate[0]}`;
  return dateIdn;
};

const removeSource = title => {
  const newTitle = title.split(' - ')[0];
  return newTitle;
};

const Card = props => {
  return (
    <Link
      href="/news/[id].js"
      as={`/news/${removeSource(props.title.toLowerCase())}`}>
      <CardWrapper>
        <CardContent>
          <CardTitle>{removeSource(props.title)}</CardTitle>
          <CardInformation>
            <span>Source: </span>
            <a href={props.url} target="_blank">
              {props.source.name.toLowerCase()}
            </a>{' '}
            | <span>Author: </span> {props.author ? props.author : 'Unknown'} |{' '}
            <span>Date: </span> {formatDateTime(props.publishedAt)}
          </CardInformation>
          <CardDescription>{props.description}</CardDescription>
        </CardContent>
        <CardThumbnail>
          <img src={props.urlToImage} alt="news-image" />
        </CardThumbnail>
      </CardWrapper>
    </Link>
  );
};

const CardWrapper = styled.div`
  width: 100%;
  height: 30vh;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2%;
  border: 1px solid #ddd;
  border-radius: 10px;
  margin-bottom: 2.5%;
  cursor: pointer;

  &:hover {
    border: 1px solid #bbb;
  }
`;

const CardContent = styled.div`
  width: 75%;
  padding-right: 5%;
  border-radius: 10px;
  cursor: pointer;
`;

const CardTitle = styled.h3`
  /* width: 75%;
  border-radius: 10px;
  cursor: pointer; */
`;

const CardInformation = styled.p`
  font-size: 1vw;
  margin-top: -2%;

  span {
    font-weight: bold;
  }

  a {
    color: black;
    text-decoration: none;
    &:hover {
      text-decoration: underline;
    }
  }
`;

const CardDescription = styled.p`
  /* width: 75%;
  border-radius: 10px;
  cursor: pointer; */
  text-align: justify;
`;

const CardThumbnail = styled.div`
  width: 25%;
  img {
    width: 100%;
  }
`;

export default Card;
