import styled from 'styled-components';

const Card = props => {
  console.log(props);

  return (
    <CardWrapper>
      <CardContent>
        <CardTitle>{props.title}</CardTitle>
        <CardInformation>
          {' '}
          {`Source: ${props.source.name.toLowerCase()}`}
        </CardInformation>
        <CardDescription></CardDescription>
      </CardContent>
      <CardThumbnail>
        <img src={props.urlToImage} alt="news-image" />
      </CardThumbnail>
    </CardWrapper>
  );
};

const CardWrapper = styled.div`
  width: 100%;
  display: flex;
  padding: 2%;
  box-sizing: border-box;
  border: 1px solid #ddd;
  border-radius: 10px;
  margin-bottom: 5%;
  cursor: pointer;

  &:hover {
    border: 1px solid #bbb;
  }
`;

const CardContent = styled.div`
  width: 75%;
  border-radius: 10px;
  cursor: pointer;
`;

const CardTitle = styled.h3`
  /* width: 75%;
  border-radius: 10px;
  cursor: pointer; */
`;

const CardInformation = styled.p`
  /* width: 75%;
  border-radius: 10px;
  cursor: pointer; */
`;

const CardDescription = styled.p`
  /* width: 75%;
  border-radius: 10px;
  cursor: pointer; */
`;

const CardThumbnail = styled.div`
  width: 25%;
  img {
    width: 100%;
  }
`;

export default Card;
