import styled from 'styled-components';

const Card = props => {
  return (
    <CardWrapper>
      <CardContent>
        <CardTitle>
          Trump adds Ken Starr and Alan Dershowitz to impeachment defense team -
          CNN
        </CardTitle>
        <CardDescription></CardDescription>
      </CardContent>
      <CardThumbnail>
        <img
          src="https://www.infodesaku.co.id/wp-content/uploads/2020/01/IMG-20200117-WA0080.jpg"
          alt="news-image"
        />
      </CardThumbnail>
    </CardWrapper>
  );
};

const CardWrapper = styled.div`
  width: 100%;
  display: flex;
  padding: 2%;
  box-sizing: border-box;
  border: 1px solid #ddd;
  border-radius: 10px;
  cursor: pointer;
`;

const CardContent = styled.div`
  width: 75%;
  border-radius: 10px;
  cursor: pointer;
`;

const CardTitle = styled.h3`
  /* width: 75%;
  border-radius: 10px;
  cursor: pointer; */
`;

const CardDescription = styled.p`
  /* width: 75%;
  border-radius: 10px;
  cursor: pointer; */
`;

const CardThumbnail = styled.div`
  width: 25%;
  img {
    width: 100%;
  }
`;

export default Card;
