import Navigation from './Navigation';
import styled from 'styled-components';

const Layout = props => {
  return (
    <LayoutContainer>
      <Navigation />
      {props.children}
    </LayoutContainer>
  );
};

const LayoutContainer = styled.div`
  color: red;
  border: 1px solid #ddd;
`;

const layoutStyle = {
  margin: 20,
  padding: 20
};

export default Layout;
