import Navigation from './Navigation';

const Layout = props => {
  return (
    <div style={layoutStyle}>
      <Navigation />
      {props.children}
    </div>
  );
};

const layoutStyle = {
  margin: 20,
  padding: 20,
  border: '1px solid #DDD'
};

export default Layout;
