import { Component, createContext } from 'react';

const Provider = createContext().Provider;

const GlobalProvider = Children => {
  return class ParentComp extends Component {
    render() {
      return (
        <Provider>
          <Children {...this.props} />
        </Provider>
      );
    }
  };
};

export default GlobalProvider;
